import React, { useState } from 'react';

function GuestMessageDetail({ message, onStartChat }) {
  const [replyMessage, setReplyMessage] = useState('');
  const [guestChat, setGuestChat] = useState([]); // ゲストからのチャットメッセージを管理

  const handleReplyChange = (e) => {
    setReplyMessage(e.target.value);
  };

  const handleSendReply = () => {
    if (replyMessage.trim() === '') {
      return;
    }

    const newChat = {
      sender: 'You',
      message: replyMessage,
    };

    setGuestChat([...guestChat, newChat]);
    setReplyMessage('');
  };

  return (
    <div>
      <h3>{message.sender}'s 依頼内容</h3>
      <p>{message.message}</p>

      {/* ゲストからのチャットメッセージを表示 */}
      {guestChat.map((chat, index) => (
        <div key={index} className="guest-chat">
          <strong>{chat.sender}:</strong> {chat.message}
        </div>
      ))}

      <div>
        <h4>返信</h4>
        <textarea
          rows="4"
          cols="50"
          value={replyMessage}
          onChange={handleReplyChange}
        ></textarea>
        <br />
        <button onClick={handleSendReply}>返信する</button>
      </div>

      {/* チャット開始ボタン */}
      <button onClick={onStartChat}>ガイドとチャット開始</button>
    </div>
  );
}

export default GuestMessageDetail;
