import React, { useState } from 'react';
import GuideMessageDetail from './GuideMessageDetail'; // インポートするコンポーネント名を修正

function GuideMessageBox() {
  const [messages, setMessages] = useState([
    {
      id: 1,
      sender: 'John',
      senderIcon: 'URL_TO_JOHN_ICON',
      message: 'ガイドの依頼です。',
    },
    {
      id: 2,
      sender: 'Alice',
      senderIcon: 'URL_TO_ALICE_ICON',
      message: 'ツアーの日程を教えてください。',
    },
    // 他のメッセージも同様に追加
  ]);

  const [selectedMessage, setSelectedMessage] = useState(null);

  const handleSelectMessage = (message) => {
    setSelectedMessage(message);
  };

  return (
    <div>
      <h2>メッセージボックス</h2>
      <div className="message-list">
        {messages.map((message) => (
          <div
            key={message.id}
            className="message"
            onClick={() => handleSelectMessage(message)}
          >
            <img
              src={message.senderIcon}
              alt={`${message.sender}'s Icon`}
              className="sender-icon"
            />
            <div className="message-content">
              <strong>{message.sender}:</strong> {message.message}
            </div>
          </div>
        ))}
      </div>
      <div className="message-input">
        {/* メッセージ入力フィールドなどのコンポーネント */}
      </div>

      {selectedMessage && <GuideMessageDetail message={selectedMessage} />}
    </div>
  );
}

export default GuideMessageBox;
