import React, { useState } from 'react';

function GuideMessageDetail({ message }) {
  const [replyMessage, setReplyMessage] = useState('');

  const handleReplyChange = (e) => {
    setReplyMessage(e.target.value);
  };

  const handleSendReply = () => {
    // 返信メッセージを送信するロジックを追加することができます。
    // ここでは、簡単なアラートメッセージを表示する例を示します。
    alert(`You replied to ${message.sender}: ${replyMessage}`);
    setReplyMessage(''); // 入力フィールドをクリア
  };

  return (
    <div>
      <h3>{message.sender}'s 依頼内容</h3>
      <p>{message.message}</p>

      <div>
        <h4>返信</h4>
        <textarea
          rows="4"
          cols="50"
          value={replyMessage}
          onChange={handleReplyChange}
        ></textarea>
        <br />
        <button onClick={handleSendReply}>返信する</button>
      </div>
    </div>
  );
}

export default GuideMessageDetail;
