import React, { useState } from 'react';

function GuideMessageBox() {
  // メッセージの一覧をステートとして保持
  const [messages, setMessages] = useState([
    {
      id: 1,
      sender: 'John',
      message: 'ガイドの依頼です。',
    },
    {
      id: 2,
      sender: 'Alice',
      message: 'ツアーの日程を教えてください。',
    },
  ]);

  // 選択されたメッセージの依頼内容を表示するステート
  const [selectedMessage, setSelectedMessage] = useState(null);

  // 新しいメッセージを入力するためのステート
  const [newMessage, setNewMessage] = useState('');

  // メッセージを送信する際の送信者を選択するステート
  const [selectedSender, setSelectedSender] = useState(''); // デフォルトは空文字列

  // 新しいメッセージを送信する関数
  const sendMessage = () => {
    if (newMessage.trim() === '' || selectedSender === '') {
      return; // 空のメッセージまたは送信者未選択の場合は送信しない
    }

    const newId = Math.max(...messages.map((message) => message.id), 0) + 1;
    const newMessageObj = {
      id: newId,
      sender: selectedSender, // ユーザーが選択した送信者
      message: newMessage,
    };

    setMessages([...messages, newMessageObj]);
    setNewMessage(''); // 入力フィールドをクリア
  };

  return (
    <div>
      <h2>メッセージボックス</h2>
      <div className="message-list">
        {messages.map((message) => (
          <div
            key={message.id}
            className="message"
            onClick={() => setSelectedMessage(message)}
          >
            <strong>{message.sender}:</strong> {message.message}
          </div>
        ))}
      </div>
      <div className="message-input">
        <input
          type="text"
          placeholder="メッセージを入力..."
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
        />
        <select
          value={selectedSender}
          onChange={(e) => setSelectedSender(e.target.value)}
        >
          <option value="">送信者を選択</option>
          <option value="John">John</option>
          <option value="Alice">Alice</option>
          {/* 他の送信者のオプションも追加 */}
        </select>
        <button onClick={sendMessage}>送信</button>
      </div>

      {selectedMessage && (
        <div>
          <h3>依頼内容</h3>
          <p>{selectedMessage.message}</p>
        </div>
      )}
    </div>
  );
}

export default GuideMessageBox;
