// GuideMyPage.js
import React, { useState } from 'react';

function GuideMyPage() {
  // ガイドのプロフィール情報をステートとして保持
  const [guideProfile, setGuideProfile] = useState({
    name: 'John Doe', // ガイドの名前
    gender: '男性', // ガイドの性別
    age: 30, // ガイドの年齢
    hourlyRate: '3000円', // ガイドの時給
    occupation: 'ガイド', // ガイドの職業
    introduction:
      'こんにちは、私はJohn Doeです。観光ガイドをしています。楽しいツアーを提供します。', // ガイドの自己紹介文
  });

  return (
    <div>
      <h2>ガイドマイページ</h2>
      <div>
        <label>名前</label>
        <span>{guideProfile.name}</span> {/* ガイドの名前を表示 */}
      </div>
      <div>
        <label>性別</label>
        <span>{guideProfile.gender}</span> {/* ガイドの性別を表示 */}
      </div>
      <div>
        <label>年齢</label>
        <span>{guideProfile.age} 歳</span> {/* ガイドの年齢を表示 */}
      </div>
      <div>
        <label>時給</label>
        <span>{guideProfile.hourlyRate}</span> {/* ガイドの時給を表示 */}
      </div>
      <div>
        <label>職業</label>
        <span>{guideProfile.occupation}</span> {/* ガイドの職業を表示 */}
      </div>
      <div>
        <label>自己紹介文</label>
        <p>{guideProfile.introduction}</p> {/* ガイドの自己紹介文を表示 */}
      </div>
      {/* 他のプロフィール情報を表示 */}
    </div>
  );
}

export default GuideMyPage;
