import React, { useState } from 'react';
import GuestMessageDetail from './GuestMessageDetail'; // ゲストメッセージ詳細コンポーネントをインポート

function GuideMessageBox() {
  const [messages, setMessages] = useState([
    {
      id: 1,
      sender: 'John',
      message: 'ガイドの依頼です。',
    },
    {
      id: 2,
      sender: 'Alice',
      message: 'ツアーの日程を教えてください。',
    },
    // 他のゲストからのメッセージも同様に追加
  ]);

  const [selectedMessage, setSelectedMessage] = useState(null);
  const [newMessage, setNewMessage] = useState('');
  const [guestChat, setGuestChat] = useState([]); // ゲストからのチャットメッセージを管理
  const [chatMessage, setChatMessage] = useState(''); // チャットメッセージのテキストエリアの値

  const sendMessage = () => {
    if (newMessage.trim() === '') {
      return;
    }

    const newId = Math.max(...messages.map((message) => message.id), 0) + 1;
    const newMessageObj = {
      id: newId,
      sender: 'You',
      message: newMessage,
    };

    setMessages([...messages, newMessageObj]);
    setNewMessage('');
  };

  const handleStartChat = () => {
    // ゲストとのチャットを開始
    const initialChat = [
      {
        sender: 'Guest',
        message: 'こんにちは、ガイドさん！',
      },
    ];
    setGuestChat(initialChat);
  };

  const handleChatInputChange = (e) => {
    setChatMessage(e.target.value);
  };

  const sendChatMessage = () => {
    if (chatMessage.trim() === '') {
      return;
    }

    const newChat = {
      sender: 'You',
      message: chatMessage,
    };

    setGuestChat([...guestChat, newChat]);
    setChatMessage('');
  };

  return (
    <div>
      <h2>メッセージボックス</h2>
      <div className="message-list">
        {messages.map((message) => (
          <div
            key={message.id}
            className="message"
            onClick={() => setSelectedMessage(message)}
          >
            <strong>{message.sender}:</strong> {message.message}
          </div>
        ))}
      </div>

      {selectedMessage && (
        <div>
          <h3>依頼内容</h3>
          <p>{selectedMessage.message}</p>
          <button onClick={handleStartChat}>ゲストとチャット開始</button>
        </div>
      )}

      {/* ゲストとのチャットメッセージを表示 */}
      {guestChat.map((chat, index) => (
        <div key={index} className="guest-chat">
          <strong>{chat.sender}:</strong> {chat.message}
        </div>
      ))}

      {/* チャットテキストエリアと送信ボタン */}
      {guestChat.length > 0 && (
        <div>
          <textarea
            rows="4"
            cols="50"
            value={chatMessage}
            onChange={handleChatInputChange}
          ></textarea>
          <button onClick={sendChatMessage}>送信</button>
        </div>
      )}
    </div>
  );
}

export default GuideMessageBox;
